<?php require_once '../../config/config.php'; require_once '../../session.php'; $uid=require_user(); $st=$pdo->prepare('SELECT ult.id,ult.status,ult.awarded_amount,ult.assigned_at,p.name,p.image,p.price FROM user_lottery_tasks ult JOIN products p ON ult.product_id=p.id WHERE ult.user_id=? ORDER BY ult.assigned_at DESC LIMIT 100'); $st->execute([$uid]); echo json_encode(['success'=>true,'tasks'=>$st->fetchAll()]); ?>