<?php
$host=getenv('DB_HOST')?:'localhost';$db=getenv('DB_NAME')?:'concentrix_db';$user=getenv('DB_USER')?:'root';$pass=getenv('DB_PASS')?:'';$charset='utf8mb4';$options=[PDO::ATTR_ERRMODE=>PDO::ERRMODE_EXCEPTION,PDO::ATTR_DEFAULT_FETCH_MODE=>PDO::FETCH_ASSOC,PDO::ATTR_EMULATE_PREPARES=>false];try{$pdo=new PDO("mysql:host=$host;dbname=$db;charset=$charset",$user,$pass,$options);}catch(PDOException $e){http_response_code(500);echo json_encode(['error'=>'DB connection failed','detail'=>$e->getMessage()]);exit;}?>